  const handleClick = () => {
    console.log('clicked')
  }
  // console.log('rendering...', counter)
  return (
    <div>
      <div>This is my counter: { counter }</div>
      <button onClick={handleClick}>Click Me!</button>
    </div>
  )
}

export default Counter;
